mapper.select:
SELECT * FROM course where course_id IN (
	SELECT course_id FROM content where content_id IN (
		SELECT content_id FROM user_course WHERE user_id = #{user_id}
	)
)

SELECT * from news where course_id in (
	SELECT course_id FROM content WHERE content_id IN (
		SELECT content_id FROM user_course WHERE user_id = #{user_id}
	)
)
